INSERT INTO `recipe` (`recipeID`, `name`, `description`, `prepTime`, `difficulty`, `serves`, `category`, `image`, `ingredients`, `method`, `prID`, `prImage`, `product`, `productGrams`) VALUES
(1, 'Dark choco flakes κρέπες', 'Μια διαφορετική συνταγή για κρέπες με γέμιση μπανάνα, σοκολάτα και Light &amp Diet Dark Chocolate!', '30', '2', '2', 'dessert', 'Crepes Light & Diet Dark Choco.JPG', '<li>200 γρ. αλεύρι για όλες της χρήσης</li>\\r\\n<li>200 γρ. σκόνη βρώμης Millhouse</li>\\r\\n<li>50 γρ. γάλα </li>\\r\\n<li>1 βανίλια σκόνη</li>\\r\\n<li>2 αυγά</li>\\r\\n<li>70 γρ. ζάχαρη</li>\\r\\n<li>Μισή πρέζα αλάτι</li>\\r\\n<li>Λίγο νερό</li>\\r\\n<li>50 γρ. βούτυρο</li>\\r\\n<li>Σοκολάτα πραλίνας φουντουκιού ή κουβερτούρα για τη γέμιση</li>\\r\\n<li>1 μπανάνα</li>\\r\\n<br>\\r\\n\\r\\nΣκεύη \\r\\n<li>1 τηγάνι</li>\\r\\n<li>2 μπωλ</li>\\r\\n<li>1 σπάτουλα</li>\\r\\n<li>1 αναδευτήρι</li>', '<p><u>Βήμα 1</u><br>\\r\\nΚάνουμε την βρώμη σκόνη στο multi. Τη ρίχνουμε σε ένα μπωλ και ανακατεύουμε καλά με το αλεύρι.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΒάζουμε σε ένα άλλο μπωλ τα αυγά, την ζάχαρη, το γάλα, την βανίλια και λίγη μπανάνα και ανακατεύουμε καλά μέχρι να γίνει ένα ομοιογενές μείγμα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΒάζουμε σιγά σιγά τη βρώμη και το αλεύρι στο μπωλ με το υπόλοιπο μείγμα και ανακατεύουμε καλά, προσέχοντας να μην κάνει σβόλους.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΒάζουμε ένα τηγάνι να ζεσταθεί με το βούτυρο, προσέχοντας να μην μας καεί. Βάζουμε το μείγμα να πάει παντού και τηγανίζουμε σε μεσαία φωτιά και από τις δύο πλευρές. \\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 5</u><br>\\r\\nΤέλος, πριν τις αφαιρέσουμε από το τηγάνι, απλώνουμε λίγη σοκολάτα για να λιώσει. Όταν είναι έτοιμη, την βάζουμε σε ένα μεγάλο πιάτο και προσθέτουμε την μπανάνα και τα δημητριακά.\\r\\n</p>\\r\\nΡολάρουμε και τρώμε.\\r\\n\\r\\n<p>Επαναλαμβάνουμε τη διαδικασία για τη δεύτερη κρέπα. </p>', 7, 'dietDark.png', 'Light &amp Diet Dark Chocolate', '60'),
(2, 'Χρυσαφένια φιλετάκια βακαλάου', 'Μια διαφορετική αλλά πεντανόστιμη συνταγή. Φιλετάκια βακαλάου παναρισμένα με Light &amp Diet Millhouse.', '30-35', '2', '2', 'salty', 'diet-coated-fish.JPG', '<li>1 φιλέτο βακαλάου</li>\\r\\n<li>200 γρ. αλεύρι</li>\\r\\n<li>300 γρ. σκόνη βρώμης Millhouse</li>\\r\\n<li>5 αυγά</li>\\r\\n<li>100 γρ. γάλα</li> \\r\\n<li>Ηλιέλαιο </li>\\r\\n<br>\\r\\n\\r\\nΣκεύη\\r\\n<li>3 δοχεία για τα μείγματα </li>\\r\\n<li>1 κατσαρόλα </li>\\r\\n<li>1 τσιμπίδα </li>\\r\\n<li>1 multi </li>\\r\\n<li>Χαρτί κουζίνας </li>', '<p><u>Βήμα 1</u><br>\\r\\nΚόβουμε το βακαλάο σε φιλετάκια των 2-3 εκατοστών.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΚάνουμε σκόνη την βρώμη στο multi και την ανακατεύουμε με το αλεύρι (μείγμα 1). Ανακατεύουμε το αυγό με το γάλα και λίγο αλατοπίπερο (μείγμα 2). Σπάμε τα Light &amp Diet λίγο λίγο (μείγμα 3). \\r\\n</p>\\r\\n\\r\\n <p><u>Βήμα 3</u><br>\\r\\nΒυθίζουμε τα φιλετάκια στα 3 μείγματα. Πρώτα στο αλεύρι, έπειτα στο αυγό και τέλος στα δημητριακά. Επαναλαμβάνουμε άλλη μια φορά στο αυγό και στα δημητριακά.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΒάζουμε σε μια κατσαρόλα ηλιέλαιο μέχρι να κάψει.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 5</u><br>\\r\\nΜε το που κάψει χαμηλώνουμε την θερμοκρασία και βυθίζουμε τα παναρισμένα φιλετάκια με προσοχή να μην σπάσουν, μέχρι να πάρουν χρυσαφένιο χρώμα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 6</u><br>\\r\\n Αφαιρούμε με μια τσιμπίδα τα φιλετάκια και τα αφήνουμε για λίγο πάνω σε ρόλο χαρτί ώστε να στραγγίξει το λάδι.\\r\\n</p>\\r\\n\\r\\nΣυνοδεύεται με σως μαγιονέζας αρωματισμένη με lime.', 11, 'diet.png', 'Light &amp Diet ', '500'),
(3, 'Φθινοπωρινή σαλάτα', 'Μια τραγανή και υγιεινή σαλάτα για όλες τις ώρες της ημέρας. Γρήγορη και πολύ θρεπτική.', '10', '1', '1', 'salty', 'diet-salad-parmesan.JPG', '<li>Μωβ μαρούλι φριζέ</li>\\r\\n<li>Ρόκα</li>\\r\\n<li>Φρέσκο σπανάκι</li>\\r\\n<li>50 γρ. flakes παρμεζάνας </li>\\r\\n<li>3 ντοματίνια</li>\\r\\n<li>1 μανταρίνι</li>\\r\\n<br>\\r\\n\\r\\nΓια τη vinaigrette:\\r\\n<li>4 κ.τ.γ. red wine vinegar</li>\\r\\n<li>5 κ.τ.σ. σιρόπι σφένδαμου</li>', '<p><u>Βήμα 1</u><br>\\r\\nΞεπλένουμε τα φύλλα από τις σαλάτες, τις στεγνώνουμε και τις ανακατεύουμε καλά. \\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΒάζουμε την σαλάτα σε ένα πιάτο και από πάνω ρίχνουμε το μανταρίνι, τα flakes παρμεζάνας, τα ντοματίνια και τις νιφάδες Light &amp Diet (όσο περισσότερες τόσο πιο τραγανή).\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΤέλος περιχύνουμε την vinaigrette στην σαλάτα.\\r\\n</p>', 11, 'diet.png', 'Light &amp Diet', '20'),
(4, 'Κρέπες Oat Super Fruits', 'Απολαυστικές κρέπες βρώμης με γέμιση από cranberry, blueberry, goji berry, μαύρη σταφίδα, μαύρη σοκολάτα και επικάλυψη μαρμελάδας blueberry.', '30', '2', '2', 'dessert', 'Crepes Oat Super Fruits.JPG', '<li>200 γρ. αλεύρι για όλες της χρήσης</li>\\r\\n<li>200 γρ. σκόνη βρώμης Millhouse</li>\\r\\n<li>50 γρ. γάλα </li>\\r\\n<li>1 βανίλια σκόνη</li>\\r\\n<li>2 αυγά</li>\\r\\n<li>70 γρ. ζάχαρη</li>\\r\\n<li>Μισή πρέζα αλάτι</li>\\r\\n<li>Λίγο νερό</li>\\r\\n<li>50 γρ. βούτυρο</li>\\r\\n<li>Μαρμελάδα blueberry</li>\\r\\n<li>Σιρόπι αγάβης</li>\\r\\n<li>Άχνη ζάχαρη (προεραιτικά)</li>\\r\\n<br>\\r\\n\\r\\n\\r\\nΣκεύη \\r\\n<li>1 τηγάνι</li>\\r\\n<li>2 μπωλ</li>\\r\\n<li>1 σπάτουλα</li>\\r\\n<li>1 αναδευτήρι</li>', '<p><u>Βήμα 1</u><br>\\r\\nΚάνουμε την βρώμη σκόνη στο multi. Τη ρίχνουμε σε ένα μπωλ και ανακατεύουμε καλά με το αλεύρι.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΒάζουμε σε ένα άλλο μπωλ τα αυγά, τη ζάχαρη, το γάλα και τη βανίλια και ανακατεύουμε καλά μέχρι να γίνει ένα ομοιογενές μείγμα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΒάζουμε σιγά σιγά τη βρώμη και το αλεύρι στο μπωλ με το υπόλοιπο μείγμα και ανακατεύουμε καλά προσέχοντας να μην κάνει σβόλους.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΒάζουμε ένα τηγάνι να ζεσταθεί με το βούτυρο, προσέχοντας να μην μας καεί. Βάζουμε το μείγμα να πάει παντού και τηγανίζουμε σε μεσαία φωτιά και από τις δύο πλευρές. \\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 5</u><br>\\r\\nΤέλος, πριν τις αφαιρέσουμε από το τηγάνι, ρίχνουμε για τη γέμιση τις Νιφάδες Βρώμης Super Fruits &amp Dark Chocolate και λίγη μαρμελάδα blueberry. Όταν είναι έτοιμη, την βάζουμε σε ένα μεγάλο πιάτο, την κλείνουμε τριγωνικά και πασπαλίζουμε με το σιρόπι και την άχνη ζάχαρη. \\r\\n</p>\\r\\n\\r\\n<p>Επαναλαμβάνουμε τη διαδικασία για τη δεύτερη κρέπα. </p>\\r\\n', 4, 'oatSuper.png', 'Νιφάδες Βρώμης, Super Fruits &amp Dark Chocolate', '60'),
(5, 'Chicken fingers', 'Τραγανιστές μπουκιές φρέσκου κοτόπουλου παναρισμένες με βιολογικές νιφάδες καλαμποκιού.', '50', '2', '2', 'salty', 'chicken-fingers-cornflakes.JPG', '<li>1 φιλέτο φρέσκο κοτόπουλο</li>\\r\\n<li>200 γρ. αλεύρι </li>\\r\\n<li>5 αυγά </li>\\r\\n<li>100 γρ. γάλα</li>\\r\\n<li>200 γρ. σκόνη βρώμης Millhouse</li>\\r\\n<li>100 γρ. παρμεζάνα τριμμένη</li>\\r\\n<br>\\r\\n\\r\\nΣκεύη\\r\\n<li>3 δοχεία για τα μείγματα </li>\\r\\n<li>1 ταψί</li>\\r\\n<li>1 αναδευτήρι</li>\\r\\n<li>1 σπάτουλα</li>\\r\\n<li>1 multi </li>\\r\\n<li>1 λαδόκολλα</li>', '<p><u>Βήμα 1</u><br>\\r\\nΠροθερμαίνουμε το φούρνο στους 200&#8451 αέρα με αντιστάσεις.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΚόβουμε το φιλέτο σε λωρίδες πάχους 2 -3 εκατοστών. Κάνουμε σκόνη την βρώμη στο multi και την ανακατεύουμε με το αλεύρι. Ανακατεύουμε το αυγό με το γάλα και λίγο αλατοπίπερο. Σπάμε τα Corn Flakes και τα ανακατεύουμε με λίγη παρμεζάνα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΠαίρνουμε ένα ένα τα φιλετάκια κοτόπουλου και τα πασπαλίζουμε με την σκόνη βρώμης. Έπειτα, τα βυθίζουμε πρώτα στο μείγμα του αυγού και στη συνέχεια στο μείγμα των Corn Flakes. Επαναλαμβάνουμε άλλη μια φορά.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΣε ένα ταψί, βάζουμε μια λαδόκολλα, όπου την βρέχουμε με λίγο λάδι. Τοποθετούμε μέσα τα φιλετάκια και τους ρίχνουμε από πάνω λίγο παρμεζάνα. Με το που τα βάλουμε μέσα στο φούρνο, κατεβάζουμε τη θερμοκρασία στους 170&#8451 και τα αφήνουμε για 30 λεπτά μέχρι να πάρουν έντονο χρυσαφί χρώμα.\\r\\n</p>', 21, 'CornFlakesBIO.png', 'Corn Flakes BIO', '500'),
(6, 'Choco Crunchy Κέικ', 'Αφράτο κέικ με γέμιση Choco Crunchy Oat και μπανάνα.', '55-60', '2', '10-12', 'dessert', 'Chοco crunchy cake.JPG', '<li>350 γρ. γάλα</li>\\r\\n<li>60 ml ελαιόλαδο </li>\\r\\n<li>150 γρ. καστανή ζαχαρη</li>\\r\\n<li>120 γρ. αλεύρι για όλες τις χρήσεις </li>\\r\\n<li>100 γρ. σκόνη βρώμης Millhouse </li>\\r\\n<li>1 κ.σ. μπέικιν πάουντερ </li>\\r\\n<li>1 αυγό</li>\\r\\n<li>Μισή μπανάνα </li>\\r\\n<br>\\r\\n\\r\\nΣκεύη \\r\\n<li>1 φόρμα για κέικ κυκλική</li>\\r\\n<li>2 μπωλ μεγάλα</li>\\r\\n<li>1 μίξερ</li>\\r\\n<li>1 πλατεία</li>', '<p><u>Βήμα 1</u><br>\\r\\nΒουτυρώνουμε την φόρμα πολύ καλά και προθερμαίνουμε το φούρνο στους 200&#8451 αέρα με αντιστάσεις. \\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΒάζουμε σε ένα μπωλ το γάλα, το ελαιόλαδο, την καστανή ζάχαρη, το αυγό, το μπέικιν πάουντερ και την μπανάνα και χτυπάμε στο μίξερ πολύ καλά μέχρι να λιώσει η μπανάνα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΣε ένα άλλο μπωλ, ανακατεύουμε το αλεύρι με την σκόνη βρώμης και τα προσθέτουμε σιγά σιγά στο υπόλοιπο μείγμα, προσέχοντας να μην κάνει σβόλους.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΒάζουμε το μισό από το τελικό μείγμα μέσα στην βουτυρωμένη φόρμα και προσθέτουμε τα 2/3 Choco Crunchy Oat. Από πάνω ρίχνουμε και το υπόλοιπο μισό μείγμα. Βάζουμε στον προ θερμασμένο φούρνο τη φόρμα και επιτόπου χαμηλώνουμε τη θερμοκρασία στους 165&#8451 μόνο αντιστάσεις.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΨήνουμε για 30 με 35 λεπτά. Μόλις φουσκώσει και ψηθεί το βγάζουμε από το φούρνο και το αφήνουμε εκτός για 10 λεπτά. \\r\\n</p> ', 2, 'crunchyOatChoco.png', 'Choco Crunchy Oat', '500'),
(12, 'Light &amp Diet Cheesecakes', 'Τραγανά, πεντανόστιμα, μοναδικά mini cheesecakes με αγνά υλικά. Μια υγιεινή συνταγή με λίγες θερμίδες.', '30', '2', '6-7', 'dessert', 'cheesecake.jpg', '<li>100 γρ. βούτυρο αγελαδινό, λιωμένο </li>\\r\\n<li>50 γρ. τριμμένο αμύγδαλο </li>\\r\\n<li>1 κ.γ. κανέλα</li>\\r\\n<br>\\r\\n\\r\\nΓια την κρέμα:\\r\\n<li>650 γρ. τυρί κρέμα</li>\\r\\n<li>70 γρ. ζάχαρη άχνη</li>\\r\\n<li>Λίγο μέλι</li>\\r\\n<li>200 γρ. κρέμα γάλακτος, 35% λιπαρά</li>\\r\\n<br>\\r\\n\\r\\nΓια την επικάλυψη:\\r\\n<li>Μαρμελάδα φράουλα ή φρούτα του δάσους</li>\\r\\n<li>1 κ.σ. χυμό λεμόνι</li>\\r\\n<br>\\r\\n\\r\\nΣκεύη\\r\\n<li>Μπλέντερ ή multi</li>\\r\\n<li>2 μπωλ</li>\\r\\n<li>Τσέρκι ζαχαροπλαστικής ή φόρμα με τοιχώματα</li>', '<p><u>Βήμα 1</u><br>\\r\\nΒάση: Τρίβουμε στο μπλέντερ ή στο multi τα δημητριακά και τα αμύγδαλα μέχρι να γίνουν σκόνη. Τα αδειάζουμε σε ένα μπωλ και ανακατεύουμε με το λιωμένο βούτυρο.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΒάζουμε τα τσέρκια ζαχαροπλαστικής σε μια πιατέλα. Εναλλακτικά, χρησιμοποιούμε φόρμες με αποσπώμενα τοιχώματα. Βάζουμε μέσα στο κάθε τσέρκι το μείγμα της βάσης πιέζοντάς το για να γίνει όσο πιο συμπαγές γίνεται. Τα βάζουμε στο ψυγείο μέχρι να ετοιμάσουμε την κρέμα. Αν θέλουμε να κάνουμε ένα μεγάλο cheesecake χρησιμοποιούμε ένα τσέρκι μεγάλο, και κάνουμε τα παραπάνω.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΚρέμα: Στον κάδο του μίξερ χτυπάμε το τυρί με λίγο μέλι για 3 λεπτά. Προσθέτουμε την κρέμα γάλακτος και συνεχίζουμε το ανακάτεμα για άλλα 3 λεπτά ώστε να ενωθούν τα υλικά και να προκύψει μια λεία κρέμα. Για κάθε cheesecake, αδειάζουμε το μείγμα πάνω από τη βάση, ισιώνουμε την επιφάνεια με μια σπάτουλα, σκεπάζουμε με αλουμινόχαρτο και βάζουμε τα γλυκό στο ψυγείο για περίπου 3-4 ώρες, μέχρι να σφίξει η κρέμα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΕπικάλυψη: Σε ένα μπωλ ανακατεύουμε τη μαρμελάδα της αρεσκείας μας με το λεμόνι. Μόλις κρυώσουν καλά τα cheesecakes στο ψυγείο, καλύπτουμε την επιφάνειά τους με το μείγμα της μαρμελάδας. Ξεφορμάρουμε και σερβίρουμε. Μπορούμε να γαρνίρουμε με φράουλες ή φρούτα του δάσους.\\r\\n</p>', 11, 'diet.png', 'Light &amp Diet', '250'),
(13, 'Choco Oat Bars', 'Σοκολατένιες μπάρες χωρίς ζάχαρη, με ψημένες νιφάδες βρώμης ολικής άλεσης και κακάο. Μια πολύ εύκολη συνταγή για όλες τις ώρες της ημέρας.', '30', '1', '8-10', 'dessert', 'oat-choco-bars.jpg', '<li>1 κούπα ξηροί καρποί (ό,τι προτιμάτε) </li>\\r\\n<li>1⁄2 κούπα βρώμη Millhouse</li>\\r\\n<li>1⁄2 κούπα μέλι</li>\\r\\n<li>1⁄2 κούπα ταχίνι</li>\\r\\n<li>2 κ.σ. φυστικοβούτυρο</li>\\r\\n<li>2 κ.σ. κακάο Choco Quick ρόφημα Millhouse</li>\\r\\n<li>1 πρέζα αλάτι</li>\\r\\n<li>1⁄4 κούπας σταφίδες (cranberries ή άλλα αποξηραμένα φρούτα) </li>\\r\\n<br>\\r\\n\\r\\nΣκεύη\\r\\n<li>1 μπωλ</li>\\r\\n<li>2 αντικολλητικά χαρτιά</li>\\r\\n<li>1 ταψί</li>', '<p><u>Βήμα 1</u><br>\\r\\nΚόβουμε σε μικρότερα κομμάτια τους ξηρούς καρπούς και τα αποξηραμένα φρούτα.\\r\\n</p>\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΤοποθετούμε όλα τα υλικά σε ένα μπωλ και ανακατεύουμε.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΤοποθετούμε το μείγμα ανάμεσα σε δύο αντικολλητικά χαρτιά σε ένα ταψί και πιέζουμε με τα χέρια μας.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΤοποθετούμε το ταψί στο ψυγείο για 1 ώρα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 5</u><br>\\r\\nΚόβουμε τις μπάρες σε ό,τι σχήμα και μέγεθος θέλουμε.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 6</u><br>\\r\\nΑπολαμβάνουμε από το ψυγείο μέχρι και για μία εβδομάδα.\\r\\n</p>\\r\\n', 33, 'Toasted_Oats.png', 'Toasted Oats', '90'),
(14, 'Μπαλίτσες ενέργειας', 'Μπουκίτσες ενέργειας με βιολογική βρώμη και φυστικοβούτυρο.', '30', '1', '15-20', 'dessert', 'oatBalls.jpg', '<li>2/3 φλιτζάνι τριμμένη καρύδα</li>\\r\\n<li>1/2 φλιτζάνι φυστικοβούτυρο</li>\\r\\n<li>1/3 φλιτζάνι μέλι</li>\\r\\n<li>1 πρέζα αλάτι</li>\\r\\n<li>1 πρέζα κανέλα</li>\\r\\n<li>1 κουταλάκι του γλυκού βανίλια</li>', '<p><u>Βήμα 1</u><br>\\r\\nΣυνδυάζουμε όλα τα υλικά σε ένα μεγάλο μπωλ.\\r\\n</p>\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΑνακατεύουμε το μείγμα και το πλάθουμε με τα χέρια μας σε ό,τι μέγεθος μπαλάκια θέλουμε.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΤο βάζουμε στο ψυγείο και απολαμβάνουμε μέχρι και για μία εβδομάδα.\\r\\n</p>\\r\\n', 5, 'oatBio.png', 'Oat Bio', '85'),
(15, 'Σαλάτα με κινόα και βρώμη', 'Ζουμερή και δροσερή σαλάτα με πολύ θρεπτικά συστατικά, κατάλληλη για ένα γρήγορο γεύμα.', '20', '1', '1', 'salty', 'Quinoa Oat salad.JPG', '<li>150 γρ. κινόα πολύχρωμη ή άσπρη</li>\\r\\n<li>1/2 λάχανο</li>\\r\\n<li>1/2 καρότο</li>\\r\\n<li>1/2 μαϊντανό</li>\\r\\n<li>Σιρόπι βαλσάμικου</li>', '<p><u>Βήμα 1</u><br>\\r\\nΒάζουμε την κινόα σε βρασμένο νερό στους 80c για 8 λεπτά, την σουρώνουμε και την κρυώνουμε με νερό.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΚόβουμε λεπτά στικς το καρότο και το λάχανο \\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΑνακατεύουμε την κινόα με το λάχανο και το καρότο και στήνουμε στο πιάτο. Τέλος, περιχύνουμε λίγο ξύδι βαλσάμικο, βρώμη και μαϊντανό.\\r\\n</p>\\r\\n', 33, 'Toasted_Oats.png', 'Toasted Oats', '20'),
(16, 'Baked Oat &amp Orange Smoothie', 'Δροσερό και δυναμωτικό smoothie με χυμό πορτοκάλι και ψημένες νιφάδες βρώμης με μέλι.', '10', '1', '!', 'smoothie', 'Baked Oat orange Smoothie.jpg', '<li>Μισό ποτήρι φρέσκο γάλα αγελάδος</li>\\r\\n<li>Φρέσκο χυμό από 3 πορτοκάλια</li>\\r\\n<li>Χυμό goji berry</li>', '<p><u>Βήμα 1</u><br>\\r\\nΤοποθετούμε όλα τα υλικά σε ένα μπλέντερ (προαιρετικά με λίγο πάγο) και τα χτυπάμε για 30 δευτερόλεπτα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΑδειάζουμε το smoothie σε ένα μεγάλο ποτήρι και απολαμβάνουμε!\\r\\n</p>\\r\\n\\r\\n<p>Εναλλακτικά, αντί για φρέσκο γάλα αγελάδος μπορούμε να προσθέσουμε γάλα καρύδας ή γάλα αμυγδάλου ή γιαούρτι.</p>\\r\\n', 33, 'Toasted_Oats.png', 'Toasted Oats', '50'),
(17, 'Φουρνιστές μπανάνες', 'Χρυσαφένιες μπανάνες, ψημένες στο φούρνο με granola και ρόδι.', '30', '1', '5-6', 'dessert', 'baked bananas.jpg', '<li>5 πολύ ώριμες μπανάνες</li>\\r\\n<li>1 φλιτζάνι βρώμης (120 γρ.) </li>\\r\\n<li>1 φλιτζάνι αμύγδαλα (200 γρ.) </li>\\r\\n<li>2 κ.σ. βούτυρο</li>\\r\\n<li>1/4 του φλιτζανιού μέλι (60 ml) </li>\\r\\n<li>1 με 2 κ.σ. κανέλα</li>\\r\\n<li>Λίγο ρόδι</li>\\r\\n<li>Λίγο αλάτι</li>\\r\\n<li>Λίγο μοσχοκάρυδο</li>\\r\\n<li>Λίγο γαρίφαλο</li>', '<p><u>Βήμα 1</u><br>\\r\\nΤοποθετούμε τα αμύγδαλα σε ένα μπλέντερ και τα χτυπάμε για περίπου ένα λεπτό. Τα προσθέτουμε σε ένα μπολ μαζί με τη βρώμη και το Crunchy Oat. Λιώνουμε το βούτυρο με το μέλι, την κανέλα, το αλάτι και τα μπαχαρικά.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΠροσθέτουμε όλα τα υλικά μαζί εκτός από τις μπανάνες στο μπολ και ανακατεύουμε.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 3</u><br>\\r\\nΚόβουμε τις μπανάνες σε φέτες και τις στρώνουμε σε ένα ταψάκι. Στη συνέχεια, απλώνουμε το μείγμα του μπολ από πάνω και ψήνομε στους 180°C για 20 λεπτά.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 4</u><br>\\r\\nΌταν πάρει ένα χρυσαφένιο χρώμα, το βγάζουμε από το φούρνο, ρίχνουμε το ρόδι και το απολαμβάνουμε ζεστό!\\r\\n</p>\\r\\n', 1, 'crunchyOat.png', 'Crunchy Oat', '200'),
(18, 'Summer Smoothie', 'Πολύ δυναμωτικό smoothie με νιφάδες βρώμης, super fruits, μπανάνα και ρόδι.', '5', '1', '1', 'smoothie', 'SummerSmoothie.jpg', '<li>1 μπανάνα</li>\\r\\n<li>Μισό ποτήρι γάλα καρύδας</li>\\r\\n<li>Μισό ρόδι</li>\\r\\n<li>Χυμό από goji berry</li>\\r\\n<li>Καρύδια</li>\\r\\n<li>Μέλι</li>', '<p><u>Βήμα 1</u><br>\\r\\nΤοποθετούμε όλα τα υλικά σε ένα μπλέντερ (προαιρετικά με λίγο πάγο) και τα χτυπάμε για 30 δευτερόλεπτα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΑδειάζουμε το smoothie σε ένα μεγάλο ποτήρι και απολαμβάνουμε!\\r\\n</p>\\r\\n\\r\\n<p>Εναλλακτικά, αντί για γάλα καρύδας μπορούμε να προσθέσουμε φρέσκο γάλα αγελάδος ή γάλα αμυγδάλου ή γιαούρτι.</p>\\r\\n', 4, 'oatSuper.png', 'Νιφάδες Βρώμης, Super Fruits & Dark Chocolate', '50'),
(19, 'Smoothie με ρόδι', 'Μια πρόταση για όλες τις ώρες της ημέρας. Θρεπτική συνταγή με πολλή ενέργεια.', '5', '1', '1', 'smoothie', 'L&D Choco Strawberry Pomegranate Smoothie.JPG', '<li>1 ρόδι</li>\\r\\n<li>Μισό ποτήρι φρέσκο γάλα αγελάδος</li>\\r\\n<li>Χυμό από cranberry</li>\\r\\n<li>Χυμό από goji berry</li>\\r\\n', '<p><u>Βήμα 1</u><br>\\r\\nΤοποθετούμε όλα τα υλικά σε ένα μπλέντερ (προαιρετικά με λίγο πάγο) και τα χτυπάμε για 30 δευτερόλεπτα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΑδειάζουμε το smoothie σε ένα μεγάλο ποτήρι και απολαμβάνουμε!\\r\\n</p>\\r\\n\\r\\n<p>Εναλλακτικά, αντί για φρέσκο γάλα αγελάδος μπορούμε να προσθέσουμε γάλα καρύδας ή γάλα αμυγδάλου ή γιαούρτι.</p>\\r\\n', 10, 'dietStrawberry.png', 'Light &amp Diet Dark Chocolate &amp Strawberry', '50'),
(20, 'Choco Crunchy Smoothie', 'Σοκολατένιο smoothie με τραγανή βρώμη, γάλα και μπανάνα. Συνταγή για πρωινό αλλά και βραδυνό.', '5', '1', '1', 'smoothie', 'Choco crunchy Smoothie.jpg', '<li>1 μπανάνα</li>\\r\\n<li>Μισό ποτήρι φρέσκο γάλα</li>\\r\\n<li>Σιρόπι αγάβης</li>\\r\\n', '<p><u>Βήμα 1</u><br>\\r\\nΤοποθετούμε όλα τα υλικά σε ένα μπλέντερ (προαιρετικά με λίγο πάγο) και τα χτυπάμε για 30 δευτερόλεπτα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΑδειάζουμε το smoothie σε ένα μεγάλο ποτήρι και απολαμβάνουμε!\\r\\n</p>\\r\\n\\r\\n<p>Εναλλακτικά, αντί για φρέσκο γάλα αγελάδος μπορούμε να προσθέσουμε γάλα καρύδας ή γάλα αμυγδάλου ή γιαούρτι.</p>\\r\\n', 2, 'crunchyOatChoco.png', 'Choco Crunchy Oat', '60'),
(21, 'Honey Rings Smoothie', 'Ένα smoothie κυρίως για παιδιά, με πολλή ενέργεια, βιταμίνες και μπισκοτένια γεύση!', '5', '1', '1', 'smoothie', 'Honey rings Smoothie.jpg', '<li>1 μπανάνα</li>\\r\\n<li>Μισό ποτήρι φρέσκο γάλα αγελάδος</li>\\r\\n<li>Χυμό goji berry</li>\\r\\n', '<p><u>Βήμα 1</u><br>\\r\\nΤοποθετούμε όλα τα υλικά σε ένα μπλέντερ (προαιρετικά με λίγο πάγο) και τα χτυπάμε για 30 δευτερόλεπτα.\\r\\n</p>\\r\\n\\r\\n<p><u>Βήμα 2</u><br>\\r\\nΑδειάζουμε το smoothie σε ένα μεγάλο ποτήρι και απολαμβάνουμε!\\r\\n</p>\\r\\n\\r\\n<p>Εναλλακτικά, αντί για φρέσκο γάλα αγελάδος μπορούμε να προσθέσουμε γάλα καρύδας ή γάλα αμυγδάλου ή γιαούρτι.</p>\\r\\n', 15, 'Honey_rings.png', 'Honey Rings', '35'),
